def is_1000_triple(a, b):
    ab = a * a + b * b
    c = math.sqrt(ab) % 1 == 0
    if c and ab != 1000:
        c_num = math.sqrt(ab)
        if a + b + c_num == 1000:
            return a * b * c_num
    return False

